mapper.select:
select a.ID as id, a.TITLE as TITLE, a.INPUTER as INPUTER, a.AUTHOR as AUTHOR, a.HITS as HITS, a.THUMB as THUMB, a.COMNUM as COMNUM, a.THUMBER as THUMBER, a.CREATETIME as CREATETIME, a.AREA as AREA, a.COMENTABLE as COMENTABLE,a.CATALOGS as CATALOGS, a.REFUSECONTENT as REFUSECONTENT, a.AUDITUSER as AUDITUSER,a.HEADPIC as HEADPIC, a.STATE as STATE, a.REMAIND1 as REMAIND1,u.name as INPUTERNAME, u.headpic as INPUTERPIC ,c.name as CATALOGNAME ,e.name as actname from article a left join user u on a.inputer=u.id left join catalog c on a.CATALOGS=c.id left join activities e on e.id=a.CATALOGS where 1=1
and LOCATE(#{condition.catalogId}, a.CATALOGS)
and a.STATE=#{condition.state}
and a.scores=#{condition.type}
and a.organid=#{condition.organid}
and a.title like concat('%',#{condition.title},'%')
order by CREATETIME DESC
limit #{from}, #{size}


    select ID, TITLE, SUBTITLE, SHORTTITLE, INPUTER, AUTHOR, HITS, THUMB, COMNUM, THUMBER, CREATETIME, 
		    AREA, COMENTABLE, EXPIRE, CATALOGS, REFUSECONTENT, AUDITUSER, SCORETYPE, SCORES, 
		    STATE, REMAIND1 from article where 1=1


        and date_format(createtime,'%Y-%m-%d')=#{createtime}


       and  title=#{title}
